import axios from 'axios';
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { ToastContainer, toast } from 'react-toastify';

function Login({ firstName, lastName }) {
    const [loginData, setLoginData] = useState({
        email: "",
        password: "",
    });
    const [isLoading, setIsLoading] = useState(false);
    const navigate = useNavigate();

    function eventHandler(event) {
        const { name, value } = event.target;
        setLoginData((prevData) => ({
            ...prevData,
            [name]: value,
        }));
    }

    const submitHandler = async (e) => {
        e.preventDefault();
        if (loginData.email === '') {
            toast.error("Email is required");
        } else if (loginData.password === '') {
            toast.error("Password is required");
        } else {
            try {
                setIsLoading(true);
                const response = await axios.post("http://localhost:3000/auth/login", { credentials: loginData });
                setIsLoading(false);
                if (response.status === 200) {
                    toast.success("Login successful");
                    navigate(`/home/${response.data.user._id}`);
                } else {
                    toast.error("Login failed");
                }
            } catch (error) {
                setIsLoading(false);
                if (error.response) {
                    toast.error(error.response.data.message);
                } else {
                    toast.error("An error occurred");
                }
            }
        }
    };

    return (
        <div className='flex flex-col w-full h-full py-2 px-12' >
            {/* ... (your existing JSX) ... */}
            <form className='flex flex-col gap-5 justify-center items-center h-full pt-28' onSubmit={submitHandler} >
                <input
                    // ... (your input JSX) ...
                />
                <input
                    // ... (your input JSX) ...
                />
                <button
                    className="w-full py-1 bg-blue-600 rounded-lg border border-stone-300 text-white"
                    disabled={isLoading}
                >
                    {isLoading ? "Logging in..." : "Login"}
                </button>
            </form>
            <ToastContainer />
        </div>
    )
}

export default Login;
